import { ADD, DELETE, SET_ITEMS } from "../types/types";

const initialState = {
  basketArr: [],
  serverData: [],
  card
};

export default (state = initialState, { type, payload }) => {
  switch (type) {
    case ADD:
      return { ...state, basketArr: [...state.basketArr, payload] };

    case DELETE:
      return {
        ...state,
        basketArr: payload,
      };
      case SET_ITEMS:
        return {
          ...state,
          serverData: payload,
        };
    default:
      return state;
  }
};
